import { Request } from 'express';

interface MulterFile {
  fieldname: string;
  originalname: string;
  encoding: string;
  mimetype: string;
  size: number;
  destination: string;
  filename: string;
  path: string;
  buffer: Buffer;
}

declare module 'express-serve-static-core' {
  interface Request {
    files?: { [fieldname: string]: MulterFile[] } | MulterFile[];
  }
}
